const downloadBtn = document.querySelector(".download-btn");
    const countdown = document.querySelector(".countdown");
    const pleaseWaitText = document.querySelector(".pleaseWait-text");
    const manualDownloadText = document.querySelector(".manualDownload-text");
    const manualDownloadLink = document.querySelector(".manualDownload-link");
    var timeLeft = 10;

    downloadBtn.addEventListener("click", () => {
      downloadBtn.style.display = "none";
      countdown.innerHTML = "Please wait for <span>" + timeLeft + "</span> second."; //for quick start of countdown
      

      var downloadTimer = setInterval(function timeCount() {
        timeLeft -= 1;
        countdown.innerHTML = " Please wait for <span>" + timeLeft + "</span> second.";
      
        if(timeLeft <= 0){
          clearInterval(downloadTimer);
          pleaseWaitText.style.display = "block";
          let download_href = " https://mega.nz/file/TNB2yBRI#AesAsPlhmod4x8b5zsZrD1xtFftaLg3jnboV7jMcCiM";
          window.location.href = download_href;
          manualDownloadLink.href = download_href;

          setTimeout(() => {
            pleaseWaitText.style.display = "none";
            manualDownloadText.style.display = "block";
          }, 4000);
        }
      },1000);
    });